amp;g-&gt;adjlist[i].vertex); g-&gt;adjlist[i].FirstEdge=NULL; g-&gt;adjlist[i].id=0; /*入度初始化为0*/ &#125; for(k=0;k&lt;g-&gt;e;k++) &#123; fscanf(fp,&quot;%d%d&quot;,&amp;i,&amp;j); s=(edgenode*)malloc(sizeof(edgenode)); s-&gt;adjvex=j; g-&gt;adjlist[j].id++; /*顶点j的入度加1*/ s-&gt;next=g-&gt;adjlist[i].FirstEdge; g-&gt;adjlist[i].FirstEdge=s; if(c == 0)&#123; //无向图 s = (edgenode *)malloc(sizeof(edgenode)); s-&gt;adjvex = i; s-&gt;next = g-&gt;adjlist[j].FirstEdge; g-&gt;adjlist[j].FirstEdge = s; &#125; &#125; fclose(fp); &#125; else //文件打开失败 g-&gt;n = 0; &#125;//拓扑排序 算法 (队列 实现)int TopSort(AovGraph g) &#123;int k=0,i,j,v, flag[M]; int queue[M]; /*队列*/ int h=0,t=0; edgenode* p; for (i=0;i&lt;g.n;i++) flag[i]=0; /*访问标记初始化*/ /*先将所有入度为0的结点进队*/ /*将程序补充完整*/ for (i=0;i&lt;g.n;i++) if (g.adjlist[i].id==0) &#123; queue[t++]=i; flag[i]=1; &#125; while (h&lt;t) &#123; v=queue[h++]; printf(&quot;%c&quot;,g.adjlist[v].vertex); k++; p=g.adjlist[v].FirstEdge; while (p) &#123; j=p-&gt;adjvex; g.adjlist[j].id--; if (g.adjlist[j].id==0 &amp;&amp; flag[j]==0) &#123; queue[t++]=j; flag[j]=1; &#125; p=p-&gt;next; &#125; &#125; return k; //返回输出的顶点个数 &#125;//打印 void print(AovGraph g)&#123; edgenode *p; int i; for (i=0;i&lt;g.n;i++) &#123; printf(&quot;%c %d &quot;, g.adjlist[i].vertex,g.adjlist[i].id); p=g.adjlist[i].FirstEdge; while (p) &#123; printf(&quot;%d--&gt;&quot;,p-&gt;adjvex); p=p-&gt;next; &#125; printf(&quot;
